import React from 'react';
import type { Meta, StoryObj } from '@storybook/angular';
import { moduleMetadata } from '@storybook/angular';
import { action } from '@storybook/addon-actions';
import { MatButtonModule } from '@angular/material/button';

const meta: Meta = {
  title: 'Generated/Button',
  decorators: [
    moduleMetadata({
      imports: [MatButtonModule],
    }),
  ],
  tags: ['autodocs'],
  parameters: { layout: 'centered' },
};

export default meta;
type Story = StoryObj;

export const Basic: Story = {
  render: () => ({
    template: `<button mat-button>Basic Button</button>`,
  }),
};

export const Raised: Story = {
  render: () => ({
    template: `<button mat-raised-button color="primary">Raised Button</button>`,
  }),
};

export const Flat: Story = {
  render: () => ({
    template: `<button mat-flat-button color="accent">Flat Button</button>`,
  }),
};

export const Stroked: Story = {
  render: () => ({
    template: `<button mat-stroked-button color="warn">Stroked Button</button>`,
  }),
};

export const Icon: Story = {
  render: () => ({
    template: `<button mat-icon-button><span>★</span></button>`,
  }),
};

export const WithClick: Story = {
  render: () => ({
    props: {
      onClick: action('button-clicked'),
    },
    template: `<button mat-raised-button color="primary" (click)="onClick($event)">Click Me</button>`,
  }),
};

export const AllVariants: Story = {
  render: () => ({
    template: `
      <div style="display: flex; gap: 16px; flex-wrap: wrap; align-items: center;">
        <button mat-button>Basic</button>
        <button mat-raised-button color="primary">Raised</button>
        <button mat-flat-button color="accent">Flat</button>
        <button mat-stroked-button>Stroked</button>
        <button mat-icon-button><span>★</span></button>
      </div>
    `,
  }),
};

export const Disabled: Story = {
  render: () => ({
    template: `
      <div style="display: flex; gap: 16px; align-items: center;">
        <button mat-raised-button color="primary" disabled>Disabled Button</button>
        <button mat-stroked-button disabled>Disabled Stroked</button>
      </div>
    `,
  }),
};
