import React from 'react';
import type { Meta, StoryObj } from '@storybook/angular';
import { moduleMetadata } from '@storybook/angular';
import { MatCardModule } from '@angular/material/card';

const meta: Meta = {
  title: 'Generated/Simple Card',
  decorators: [
    moduleMetadata({
      imports: [MatCardModule],
    }),
  ],
  parameters: {
    layout: 'centered',
  },
} satisfies Meta;

export default meta;
type Story = StoryObj<typeof meta>;

export const Default: Story = {
  render: () => ({
    template: `
      <mat-card style="max-width: 400px;">
        <mat-card-header>
          <mat-card-title>Card Title</mat-card-title>
        </mat-card-header>
        <mat-card-content>
          <p>This is a simple card with a title and description. It demonstrates the basic usage of Angular Material's card component.</p>
        </mat-card-content>
      </mat-card>
    `,
  }),
};

export const WithLongerContent: Story = {
  render: () => ({
    template: `
      <mat-card style="max-width: 400px;">
        <mat-card-header>
          <mat-card-title>Welcome to Angular Material</mat-card-title>
        </mat-card-header>
        <mat-card-content>
          <p>Angular Material provides a comprehensive set of UI components that follow Material Design principles. This card component is perfect for displaying grouped content in a structured and visually appealing way.</p>
        </mat-card-content>
      </mat-card>
    `,
  }),
};

